mapper.select:
select * from t_video where 1=1
and area = #{area}
order by id desc limit #{start}, #{limit}

mapper.insert:
insert into
    t_video_like(
    userId,
    videoId,
    createTime
)values(
           #{userId},
           #{videoId},
           #{createTime}
       )

mapper.delete:
delete from
    t_video_like
where
    videoId = #{videoId}
  and userId = #{userId}

delete from
    t_video_collection
where
    videoId = #{videoId}
  and userId = #{userId}

mapper.update:
update
    t_video_coin
set
    amount = #{amount},
    updateTime = #{updateTime}
where
    videoId = #{videoId}
  and userId = #{userId}
